  } 
  
  if(realistic_ind == 0){
    print("Optimistic or Pessimistic uptake")
    vax_assignment$vax_val <- vax_assignment$vax
    vax_assignment$vaccine_wave_val <- if_else(vax_assignment$vax_val == 1, vax_assignment$vaccine_wave + 365, 0)
    
